{let n=function(){let n=$("#new-comment-form");n.submit(function(o){o.preventDefault(),$.ajax({type:"post",url:"/comments/create",data:n.serialize(),success:function(n){let o=e(n.data.comment);$(" #comments-container>ul").prepend(o),t($(" .delete-comment-button",o))},error:function(n){console.log(n.responseText)}})})},e=function(n){return $(`<li class="comment-${n._id}">\n        <div class="comment-author-details">\n            <div class="comment-author-name">\n                <small>\n                    <i class="fas fa-user fa-2x"></i>\n                    ${n.user.name}\n                </small>\n            </div>\n            <div class="comment-delete-button-container">\n                <a class="delete-comment-button" href="/comments/destroy/${n._id}">\n                    <i class="far fa-window-close fa-2x"></i>\n                    </a>\n            </div>\n            </div>\n            <div class="comment-content">\n                <p>\n                ${n.content}\n                </p> \n            </div>  \n    </li>`)},t=function(n){$(n).click(function(e){e.preventDefault(),$.ajax({type:"get",url:$(n).prop("href"),success:function(n){console.log(n),$(`.comment-${n.data.comment_id}`).remove()},error:function(n){console.log(n.responseText)}})})},o=function(){var n=$(" .delete-comment-button");for(i of n)t(i)};n(),o()}