class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.newCommentForm.submit(function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newDomComment(n.data.comment);$(`#post-comments-${e}`).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"metroui",text:"Comment Published",type:"success",layout:"topRight",timeout:1500,closeWith:["click","button"]}).show()},error:function(e){console.log(e.responseText)}})})}newDomComment(e){return $(`<li id="comment-${e._id}">\n        <div class="comment-author-details">\n            <div class="comment-author-name">\n                <small>\n                    <i class="fas fa-user fa-2x"></i>\n                    ${e.user.name}\n                </small>\n            </div>\n            <div class="comment-delete-button-container">\n                <a class="delete-comment-button" href="/comments/destroy/${e._id}">\n                    <i class="far fa-window-close fa-2x"></i>\n                    </a>\n            </div>\n            </div>\n            <div class="comment-content">\n                <p>\n                ${e.content}\n                </p> \n            </div> \n        <div class="likes-container"> \n        <small>\n        <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n        <i class="far fa-thumbs-up fa-2x"></i> 0 Likes\n        </a>\n        </small>\n       </div> \n    </li>`)}deleteComment(e){$(e).click(function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){console.log(e),$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"metroui",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500,closeWith:["click","button"]}).show()},error:function(e){console.log(e.responseText)}})})}}